import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [registros, setRegistros] = useState([]);
  const [registroSeleccionado, setRegistroSeleccionado] = useState(null);

  useEffect(() => {
    cargarRegistros();
  }, []);

  const cargarRegistros = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/registros');
      const data = await response.json();
      setRegistros(data);
    } catch (error) {
      console.error('Error al cargar los registros:', error);
    }
  };

  const renderRegistros = () => {
    return (
      <div className="row">
        {registros.map(registro => (
          <div key={registro.id} className="col-md-3 mb-3">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Registro #{registro.id}</h5>
                <p className="card-text">Nombre: {registro.nombre}</p>
                <p className="card-text">Instituto: {registro.instituto}</p>
                <p className="card-text">Proyecto: {registro.proyecto}</p>
                <button className="btn btn-primary" onClick={() => mostrarDetalles(registro)}>Ver detalles</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  const mostrarDetalles = (registro) => {
    setRegistroSeleccionado(registro);
  };

  const renderDetalles = () => {
    const detalles = [];
    for (const key in registroSeleccionado) {
      if (key !== 'created_at' && key !== 'updated_at') {
        let valor = registroSeleccionado[key];
        if (key === 'fecha_inicio' || key === 'fecha_fin') {
          valor = new Date(valor).toLocaleDateString('es-MX', { year: 'numeric', month: 'long', day: 'numeric' });
        }
        if (key === 'fecha_inicio') {
          detalles.push(
            <div key={key} className="col-md-3">
              <strong>{`Periodo de estancia: De: `}</strong> {valor} 
            </div>
          );
        } else if (key === 'fecha_fin') {
          detalles.push(
            <div key={key} className="col-md-3">
              <strong>{` hasta: `}</strong> {valor} 
            </div>
          );
        } else {
          detalles.push(
            <div key={key} className="col-md-3">
              <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {valor}
            </div>
          );
        }
      }
    }
    return detalles;
  };

  return (
    <div className="container mt-5">
      <h1>Administrador de Registros</h1>
      {registroSeleccionado ? (
        <>
          <h2>Detalles del Registro #{registroSeleccionado.id}</h2>
          <div className="row">
            {renderDetalles()}
          </div>
          <button className="btn btn-secondary mt-3" onClick={() => setRegistroSeleccionado(null)}>Volver</button>
        </>
      ) : renderRegistros()}
    </div>
  );
}

export default AdminDashboard;
