import React, { useState } from 'react';

function RegistroForm() {
  const [nombre, setNombre] = useState('');
  const [cvu, setCvu] = useState('');
  const [instituto, setInstituto] = useState('');
  const [pais, setPais] = useState('');
  const [telefono, setTelefono] = useState('');
  const [correo, setCorreo] = useState('');
  const [tipoEstancia, setTipoEstancia] = useState('');
  const [responsable, setResponsable] = useState('');
  const [proyecto, setProyecto] = useState('');
  const [justificacion, setJustificacion] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [curp, setCurp] = useState('');
  const [solicitud, setSolicitud] = useState(false); // Cambiado a booleano
  const [mensaje, setMensaje] = useState('');

  async function handleSubmit(event) {
    event.preventDefault();
    console.warn(nombre, cvu, instituto, pais, telefono, correo, tipoEstancia, responsable, proyecto, justificacion, fechaInicio, fechaFin, curp, solicitud);
    
    try {
      const response = await fetch("http://localhost:8000/api/addregistro", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          nombre,
          cvu,
          instituto,
          pais,
          telefono,
          correo,
          tipoEstancia,
          responsable,
          proyecto,
          justificacion,
          fechaInicio,
          fechaFin,
          curp,
          solicitud
        })
      });

      if (response.ok) {
        setMensaje('Registro enviado correctamente');
      } else {
        setMensaje('Error al enviar el registro');
      }
    } catch (error) {
      console.error('Error al enviar el formulario:', error);
      setMensaje('Error al enviar el formulario');
    }
  }

  return (
    <div className="container">
      <h1 className="mt-5">Formulario de Registro</h1>
      <form onSubmit={handleSubmit}>
        {/* Otros campos del formulario aqu√≠ */}
        <div className="row mt-4">
          <div className="col-md-6">
            <label htmlFor="curp" className="form-label">CURP:</label>
            <input type="text" id="curp" className="form-control" value={curp} onChange={(e) => setCurp(e.target.value)} />
          </div>
          <div className="col-md-6">
            <div className="form-check mt-4">
              <input className="form-check-input" type="checkbox" id="solicitud" checked={solicitud} onChange={(e) => setSolicitud(e.target.checked)} />
              <label className="form-check-label" htmlFor="solicitud">
                Solicitud:
              </label>
            </div>
          </div>
        </div>
        <div className="text-center mt-5">
          <button type="submit" className="btn btn-primary">Enviar</button>
        </div>
      </form>
      {mensaje && <p className="text-center mt-3">{mensaje}</p>}
    </div>
  );
}

export default RegistroForm;
