import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [registros, setRegistros] = useState([]);
  const [registroSeleccionado, setRegistroSeleccionado] = useState(null);

  useEffect(() => {
    cargarRegistros();
  }, []);

  const cargarRegistros = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/registros');
      const data = await response.json();
      setRegistros(data);
    } catch (error) {
      console.error('Error al cargar los registros:', error);
    }
  };

  const verDetalles = (registro) => {
    setRegistroSeleccionado(registro);
  };

  const renderDetalles = () => {
    const detalles = [];
    for (const key in registroSeleccionado) {
      if (key !== 'created_at' && key !== 'updated_at') {
        detalles.push(
          <div key={key} className="col-md-3">
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {registroSeleccionado[key]}
          </div>
        );
      }
    }
    return detalles;
  };

  return (
    <div className="container mt-5">
      <h1>Administrador de Registros</h1>
      <div className="row">
        <div className="col-md-12">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre del Solicitante</th>
                <th>Instituto</th>
                <th>Proyecto</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {registros.map((registro) => (
                <tr key={registro.id}>
                  <td>{registro.id}</td>
                  <td>{registro.nombre}</td>
                  <td>{registro.instituto}</td>
                  <td>{registro.proyecto}</td>
                  <td>
                    <button className="btn btn-primary" onClick={() => verDetalles(registro)}>Ver Detalles</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {registroSeleccionado && (
        <div className="row">
          <div className="col-md-12">
            <div className="card">
              <div className="card-header">Detalles del Registro</div>
              <div className="card-body">
                <div className="row">
                  {renderDetalles()}
                </div>
                <button className="btn btn-secondary" onClick={() => setRegistroSeleccionado(null)}>Cerrar</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
