import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [registros, setRegistros] = useState([]);
  const [registroSeleccionado, setRegistroSeleccionado] = useState(null);
  const [registroEditable, setRegistroEditable] = useState(null);

  useEffect(() => {
    cargarRegistros();
  }, []);

  const cargarRegistros = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/registros');
      const data = await response.json();
      setRegistros(data);
    } catch (error) {
      console.error('Error al cargar los registros:', error);
    }
  };

  const verDetalles = (registro) => {
    setRegistroSeleccionado(registro);
  };

  const editarRegistro = (registro) => {
    setRegistroEditable(registro);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRegistroEditable({ ...registroEditable, [name]: value });
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch(`http://localhost:8000/api/registros/${registroEditable.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(registroEditable),
      });
      if (response.ok) {
        console.log('Registro editado exitosamente');
        // Recargar la lista de registros después de editar
        cargarRegistros();
      } else {
        console.error('Error al editar el registro:', response.statusText);
      }
    } catch (error) {
      console.error('Error al editar el registro:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5">Administrador de Registros</h1>
      <div className="row">
        <div className="col-md-12">
          <table className="table table-striped mb-5">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre del Solicitante</th>
                <th>Instituto</th>
                <th>Proyecto</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {registros.map((registro) => (
                <tr key={registro.id}>
                  <td>{registro.id}</td>
                  <td>{registro.nombre}</td>
                  <td>{registro.instituto}</td>
                  <td>{registro.proyecto}</td>
                  <td>
                    <button className="btn btn-primary me-2" onClick={() => verDetalles(registro)}>Ver Detalles</button>
                    <button className="btn btn-success" onClick={() => editarRegistro(registro)}>Editar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {registroSeleccionado && (
        <div className="row mt-5">
          <div className="col-md-12">
            <div className="card mb-5">
              <div className="card-header text-center">Detalles del Registro</div>
              <div className="card-body">
                <div className="row">
                  <div className="col-md-6">
                    <strong>Nombre del Solicitante:</strong> {registroSeleccionado.nombre}
                  </div>
                  <div className="col-md-6">
                    <strong>Instituto:</strong> {registroSeleccionado.instituto}
                  </div>
                  <div className="col-md-6">
                    <strong>Proyecto:</strong> {registroSeleccionado.proyecto}
                  </div>
                  {/* Agregar más detalles del registro aquí si es necesario */}
                </div>
                <button className="btn btn-secondary mt-3 float-end" onClick={() => setRegistroSeleccionado(null)}>Cerrar</button>
              </div>
            </div>
          </div>
        </div>
      )}
      {registroEditable && (
        <div className="row mt-5">
          <div className="col-md-6 offset-md-3">
            <div className="card">
              <div className="card-header text-center">Editar Registro</div>
              <div className="card-body">
                <form>
                  <div className="mb-3">
                    <label htmlFor="nombre" className="form-label">Nombre del Solicitante:</label>
                    <input type="text" className="form-control" id="nombre" name="nombre" value={registroEditable.nombre} onChange={handleChange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="instituto" className="form-label">Instituto:</label>
                    <input type="text" className="form-control" id="instituto" name="instituto" value={registroEditable.instituto} onChange={handleChange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="proyecto" className="form-label">Proyecto:</label>
                    <input type="text" className="form-control" id="proyecto" name="proyecto" value={registroEditable.proyecto} onChange={handleChange} />
                  </div>
                  {/* Agregar más campos editables aquí si es necesario */}
                  <div className="text-center">
                    <button type="button" className="btn btn-primary" onClick={handleSubmit}>Guardar Cambios</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
