import React, { useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const InvestigadorDashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Verificar si el token está presente en el localStorage
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('No se encontró ningún token en el almacenamiento local');
    } else {
      console.log('Token encontrado:', token);
    }
  }, []);

  const handleLogout = () => {
    // Limpiar las credenciales del usuario (por ejemplo, eliminar tokens de sesión)
    localStorage.removeItem('token'); // Eliminar el token de sesión almacenado en el almacenamiento local

    // Redirigir al usuario a la página de inicio de sesión
    navigate('/login');
  };

  const handleFormulario = () => {
    // Redirigir al usuario a la pantalla del formulario original
    navigate('/formulario');
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-header">
              <h3>Bienvenido al Dashboard de Investigador</h3>
            </div>
            <div className="card-body">
              <p>¡Hola, usuario! Bienvenido al dashboard de investigador.</p>
            </div>
            <div className="card-footer text-end">
              <Button variant="primary" onClick={handleLogout}>Cerrar Sesión</Button>
              {/* Botón para ir al formulario original */}
              <Button variant="success" className="ms-2" onClick={handleFormulario}>Ir al Formulario</Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InvestigadorDashboard;
