import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [registros, setRegistros] = useState([]);
  const [registroSeleccionado, setRegistroSeleccionado] = useState(null);
  const [edicionAbierta, setEdicionAbierta] = useState(false);
  const [registroEditable, setRegistroEditable] = useState(null);

  useEffect(() => {
    cargarRegistros();
  }, []);

  const cargarRegistros = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/registros');
      const data = await response.json();
      setRegistros(data);
    } catch (error) {
      console.error('Error al cargar los registros:', error);
    }
  };

  const verDetalles = (registro) => {
    setRegistroSeleccionado(registro);
  };

  const editarRegistro = (registro) => {
    setRegistroEditable(registro); // Establece el registro editable con los datos del registro seleccionado
    setEdicionAbierta(true);
  };

  const handleCloseEdicion = () => {
    setEdicionAbierta(false);
    setRegistroEditable(null);
  };

  const handleSubmitEdicion = () => {
    // Aquí puedes implementar la lógica para enviar los datos editados al servidor
    console.log('Registro editado:', registroEditable);
    handleCloseEdicion();
  };

  const renderDetalles = () => {
    const detalles = [];
    for (const key in registroSeleccionado) {
      if (key !== 'created_at' && key !== 'updated_at') {
        let valor = registroSeleccionado[key];
        if (key === 'fecha_inicio' || key === 'fecha_fin') {
          valor = new Date(valor).toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit', year: 'numeric' });
          detalles.push(
            <div key={key} className="col-md-6">
              <strong>Periodo de estancia:</strong> De: {valor} hasta: {registroSeleccionado['fecha_fin']}
            </div>
          );
        } else {
          detalles.push(
            <div key={key} className="col-md-6">
              <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {valor}
            </div>
          );
        }
      }
    }
    return detalles;
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5">Administrador de Registros</h1>
      <div className="row">
        <div className="col-md-12">
          <table className="table table-striped mb-5">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre del Solicitante</th>
                <th>Instituto</th>
                <th>Proyecto</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {registros.map((registro) => (
                <tr key={registro.id}>
                  <td>{registro.id}</td>
                  <td>{registro.nombre}</td>
                  <td>{registro.instituto}</td>
                  <td>{registro.proyecto}</td>
                  <td>
                    <button className="btn btn-primary me-2" onClick={() => verDetalles(registro)}>Ver Detalles</button>
                    <button className="btn btn-success" onClick={() => editarRegistro(registro)}>Editar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {registroSeleccionado && (
        <div className="row mt-5">
          <div className="col-md-12">
            <div className="card mb-5">
              <div className="card-header text-center">Detalles del Registro</div>
              <div className="card-body">
                <div className="row">
                  {renderDetalles()}
                </div>
                <button className="btn btn-secondary mt-3 float-end" onClick={() => setRegistroSeleccionado(null)}>Cerrar</button>
              </div>
            </div>
          </div>
        </div>
      )}
      {edicionAbierta && registroEditable && ( // Solo muestra el modal si hay un registro editable
        <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block', backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Editar Registro</h5>
                <button type="button" className="btn-close" aria-label="Close" onClick={handleCloseEdicion}></button>
              </div>
              <div className="modal-body">
                <form>
                  <div className="mb-3">
                    <label htmlFor="nombre" className="form-label">Nombre del Solicitante:</label>
                    <input type="text" className="form-control" id="nombre" value={registroEditable.nombre} onChange={(e) => setRegistroEditable({ ...registroEditable, nombre: e.target.value })} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="instituto" className="form-label">Instituto:</label>
                    <input type="text" className="form-control" id="instituto" value={registroEditable.instituto} onChange={(e) => setRegistroEditable({ ...registroEditable, instituto: e.target.value })} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="proyecto" className="form-label">Proyecto:</label>
                    <input type="text" className="form-control" id="proyecto" value={registroEditable.proyecto} onChange={(e) => setRegistroEditable({ ...registroEditable, proyecto: e.target.value })} />
                  </div>
                  {/* Agregar más campos editables aquí si es necesario */}
                </form>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseEdicion}>Cancelar</button>
                <button type="button" className="btn btn-primary" onClick={handleSubmitEdicion}>Guardar Cambios</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
