import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [registros, setRegistros] = useState([]);

  useEffect(() => {
    cargarRegistros();
  }, []);

  const cargarRegistros = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/registros');
      const data = await response.json();
      setRegistros(data);
    } catch (error) {
      console.error('Error al cargar los registros:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1>Administrador de Registros</h1>
      <div className="row">
        <div className="col-md-12">
          <table className="table">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>CVU</th>
                <th>Instituto</th>
                <th>País</th>
                <th>Teléfono</th>
                <th>Correo</th>
                <th>Tipo de Estancia</th>
                <th>Responsable</th>
                <th>Proyecto</th>
                <th>Justificación</th>
                <th>Fecha de Inicio</th>
                <th>Fecha de Fin</th>
                <th>CURP</th>
                <th>Solicitud</th>
                {/* Agrega más encabezados según las columnas de tu tabla de registros */}
              </tr>
            </thead>
            <tbody>
              {registros.map((registro) => (
                <tr key={registro.id}>
                  <td>{registro.nombre}</td>
                  <td>{registro.cvu}</td>
                  <td>{registro.instituto}</td>
                  <td>{registro.pais}</td>
                  <td>{registro.telefono}</td>
                  <td>{registro.correo}</td>
                  <td>{registro.tipoEstancia}</td>
                  <td>{registro.responsable}</td>
                  <td>{registro.proyecto}</td>
                  <td>{registro.justificacion}</td>
                  <td>{registro.fecha_inicio}</td>
                  <td>{registro.fecha_fin}</td>
                  <td>{registro.curp}</td>
                  <td>{registro.solicitud}</td>
                  {/* Agrega más columnas según las propiedades de tu objeto de registro */}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
